						["Destructor"] = "function (self, unitId, unitFrame, envTable)\n    if (unitFrame.DeliriumPlayerNameText) then\n        unitFrame.DeliriumPlayerNameText.show = false \n    end\n    \n    unitFrame.IsHiddenByDelirium = nil\n    unitFrame:Show()\nend\n\n\n",
						["Constructor"] = "function (self, unitId, unitFrame, envTable)\n    \n    -- Copy of Tercioo's https://wago.io/wfh6-4YDU \"Uu'nat - Lunacy\", adjusted for Za'qlu Delirium\n    --if this is true, hide only the nameplate and show the player name as a red text indicating the effect of Delirium is active.\n    envTable.ShowNameOnly = false\n    \n    --if this is true, hide everything\n    envTable.FullHide = true\n    \n    --create the player name text if doesn't exists\n    if (not unitFrame.DeliriumPlayerNameText) then\n        unitFrame.DeliriumPlayerNameText = Plater:CreateLabel (unitFrame.PlateFrame)\n        unitFrame.DeliriumPlayerNameText:SetPoint ('center', 0, 0)\n    end\n    \n    --Delirium debuff localized name\n    envTable.LocalizedDebuffName = GetSpellInfo (295249) --lunacy spellId\n    --envTable.LocalizedDebuffName = GetSpellInfo (292711) --test (druid sunfire)\n    \n    --setup the player name text\n    unitFrame.DeliriumPlayerNameText.textsize = 8\n    unitFrame.DeliriumPlayerNameText.textcolor = \"red\"\n    unitFrame.DeliriumPlayerNameText.outline = \"OUTLINE\"\n    unitFrame.DeliriumPlayerNameText.show = false    \n    \n    --unitFrame.DeliriumPlayerNameText.textfont = \"Arial Narrow\"\n    \n    --private\n    function envTable.GetPetOwner (unitFrame)\n        --query details! to know if the unit on this nameplate is a pet of a player\n        --not using the new Details API since players might have old versions of Details! installed\n        if (not Details) then\n            return false \n        end\n        \n        local guid = unitFrame.namePlateUnitGUID\n        local segment = Details:GetCurrentCombat()\n        \n        if (segment) then\n            for _, actorObject in ipairs (segment[1]._ActorTable) do\n                if (actorObject.serial == guid) then\n                    local owner = actorObject.owner\n                    if (owner and owner:IsPlayer()) then\n                        return owner\n                    end\n                end\n            end\n        end        \n    end\n    \n    function envTable.UpdateNameplate (unitFrame, unitId)\n        \n        --check if the unit has the debuff\n        local hasAura = false\n        \n        local unitToCheck = unitId\n        \n        --check if the unit is a pet\n        if (Plater.GetUnitType (unitFrame.PlateFrame) == \"pet\") then\n            --attempt ot get the pet owner from Details! Damage Meter\n            local petOwner = envTable.GetPetOwner (unitFrame)\n            if (petOwner) then\n                local unitGUID = petOwner.serial\n                unitToCheck = nil\n                for _, plateFrame in ipairs (Plater.GetAllShownPlates()) do\n                    if (plateFrame.namePlateUnitGUID == unitGUID) then\n                        unitToCheck = plateFrame.namePlateUnitToken\n                        break\n                    end\n                end\n            end\n        end        \n        \n        if (not unitToCheck) then\n            unitFrame:Show()\n            return \n        end\n        \n        for i = 1, 40 do\n            local auraName = UnitDebuff (unitToCheck, i)\n            if (not auraName) then\n                break \n            end\n            \n            if (auraName == envTable.LocalizedDebuffName) then\n                hasAura = true\n                break\n            end\n        end\n        \n        if (hasAura) then\n            --can show the player name?\n            if (unitFrame.DeliriumPlayerNameText and envTable.ShowNameOnly and not envTable.FullHide) then\n                unitFrame.DeliriumPlayerNameText.show = true\n                unitFrame.DeliriumPlayerNameText.text = unitFrame.namePlateUnitName\n            end    \n            \n            --hide the nameplate widgets\n            unitFrame:Hide()\n            \n            unitFrame.IsHiddenByDelirium = true\n        else\n            if (unitFrame.IsHiddenByDelirium) then\n                unitFrame:Show()\n                \n                if (unitFrame.DeliriumPlayerNameText) then\n                    unitFrame.DeliriumPlayerNameText.show = false\n                end\n                \n                unitFrame.IsHiddenByDelirium = nil\n            end\n        end\n        \n    end \nend",
