					["Hooks"] = {
						["Player Logon"] = "function (modTable)\n    modTable.UpdateEXETalent()\nend",
						["Player Talent Update"] = "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.UpdateEXETalent()\nend",
						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    if UnitIsTapDenied (unitId) or unitFrame.IsSelf or unitFrame.PlayerCannotAttack then return end\n    \n    if (modTable.EXEenabled) then  \n        local healthBar = unitFrame.healthBar\n        local percent = healthBar.CurrentHealth / healthBar.CurrentHealthMax\n        local alert = modTable.EXEpercent\n        \n        if (percent <= modTable.EXEpercent) then\n            envTable.InExecuteRange = true\n            if (envTable.InExecuteAlertRange) then\n                envTable.InExecuteAlertRange = false\n            end\n        elseif (percent < modTable.SRS and percent >= modTable.SRE) then\n            envTable.InExecuteRange = true\n            alert = modTable.SRE\n            if (envTable.InExecuteAlertRange) then\n                envTable.InExecuteAlertRange = false\n            end\n        elseif modTable.EXEpercent ~= 0 and (percent > modTable.EXEpercent and percent <= modTable.EXEpercent + modTable.config.EXEalert/100) then\n            envTable.InExecuteAlertRange = true\n            if (envTable.InExecuteRange) then\n                envTable.InExecuteRange = false\n            end            \n        else\n            if (envTable.InExecuteRange) then\n                envTable.InExecuteRange = false\n            end\n            if (envTable.InExecuteAlertRange) then\n                envTable.InExecuteAlertRange = false\n            end\n        end\n        \n        if (envTable.InExecuteRange) then \n            envTable.UnitInExecuteRange(unitFrame)\n            envTable.UnitInExecuteAlertRange (unitFrame, alert)\n        else\n            if modTable.config.EXEhbcon then\n                Plater.RefreshNameplateColor (unitFrame)\n            end                \n            if modTable.config.EXEbdron then\n                Plater.SetBorderColor (unitFrame)\n            end                \n            \n            if not modTable.config.EXEhdoff and (envTable.InExecuteAlertRange) then\n                envTable.UnitInExecuteAlertRange (unitFrame, alert)\n            else\n                unitFrame.healthBar.healthCutOff:Hide() \n                unitFrame.healthBar.executeRange:Hide()\n            end\n        end\n    else\n        if (unitFrame.InExecuteRange) then\n            envTable.UnitInExecuteRange (unitFrame, 0)\n        end\n    end\nend",
						["Initialization"] = "function (modTable)\n    \n    function modTable.legendaryCheck()\n        local equipped = false\n        local slot = {\n            5, -- foot\n            15, -- back\n        }\n        local legendary = {\n            [6927] = true, -- arcane pressure\n        }\n        \n        for i = 1, 2 do\n            local item = Item:CreateFromEquipmentSlot(slot[i])\n            if not item:IsItemEmpty() then\n                local location = item:GetItemLocation()\n                local quality = C_Item.GetItemQuality(location)\n                if quality == 5 then\n                    local itemLink = C_Item.GetItemLink(location)\n                    if itemLink then\n                        local itemSplit = {select(13,strsplit(\":\", string.match(itemLink, \"item:([%-?%d:]+)\")))}\n                        for index = 2, (tonumber(itemSplit[1]) or 0) + 1 do\n                            local bonus = tonumber(itemSplit[index])\n                            if legendary[bonus] then equipped = true break end\n                        end\n                    end\n                end\n            end\n        end\n        \n        return equipped\n    end\n    \n    function modTable.UpdateEXETalent()\n        modTable.EXEenabled = false\n        modTable.EXEpercent = 0\n        modTable.SRS = 1\n        modTable.SRE = 1\n        \n        if modTable.config.EXEkorayn and C_Soulbinds.GetActiveSoulbindID() == 6 then\n            if C_Soulbinds.GetNode(1203).state == 3 then\n                modTable.EXEenabled = true\n                modTable.SRS = 0.999\n                modTable.SRE = 0.9\n            end\n            \n            if C_Soulbinds.GetNode(1854).state == 3 then\n                modTable.EXEpercent = 0.35\n            end\n        end\n        \n        local _, class = UnitClass (\"player\")\n        local spec = GetSpecialization()\n        local specID = GetSpecializationInfo (spec) or 0\n        if not (spec or class or specID) or specID == 0 then return end\n        \n        if (class == \"MAGE\") then\n            if (specID == 62) then \n                if (modTable.legendaryCheck()) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.35\n                end\n            elseif (specID == 63) then \n                local _, _, _, using_Firestarter = GetTalentInfo (1, 1, 1)\n                local _, _, _, using_Touch = GetTalentInfo (1, 3, 1)\n                if (using_Firestarter) then\n                    modTable.EXEenabled = true\n                    modTable.SRS = 0.999\n                    modTable.SRE = 0.9\n                elseif (using_Touch) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.3\n                end\n            end\n        elseif (class == \"WARLOCK\") then\n            if (specID == 265) then \n                local _, _, _, using_Souldrain = GetTalentInfo (1, 3, 1)\n                if (using_Souldrain) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.2\n                end\n            elseif (specID == 267) then \n                local _, _, _, using_Shadowburn = GetTalentInfo (2, 3, 1)\n                if (using_Shadowburn) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.2\n                end\n            end\n        elseif (class == \"PRIEST\") then\n            if IsSpellKnown(32379) then\n                modTable.EXEenabled = true\n                modTable.EXEpercent = 0.2\n                if (specID == 258) then --shadow\n                    local _, _, _, using_ToF = GetTalentInfo (3, 1, 1)\n                    if (using_Tof) then\n                        modTable.EXEpercent = 0.35\n                    end\n                end\n            end\n        elseif (class == \"WARRIOR\") then\n            if IsSpellKnown(163201) then\n                modTable.EXEenabled = true\n                modTable.EXEpercent = 0.2\n                if (specID == 71 or specID == 72) then\n                    local _, _, _, using_Massacre = GetTalentInfo (3, 1, 1)\n                    if (using_Massacre) then\n                        modTable.EXEpercent = 0.35\n                    end\n                end   \n                if IsSpellKnown(317483) then\n                    modTable.SRS = 0.999\n                    modTable.SRE = 0.8\n                end\n            end\n        elseif (class == \"HUNTER\") then\n            if IsSpellKnown(53351) then\n                modTable.EXEenabled = true\n                modTable.EXEpercent = 0.2\n            end   \n            if (specID == 252) then\n                local _, _, _, using_CarefulAim = GetTalentInfo (2, 1, 1)\n                if (using_CarefulAim) then\n                    modTable.EXEenabled = true\n                    modTable.SRS = 0.999\n                    modTable.SRE = 0.7\n                end\n            elseif (specID == 253) then\n                local _, _, _, using_KillerInstinct = GetTalentInfo (1, 1, 1)\n                if (using_KillerInstinct) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.35\n                end\n            end\n        elseif (class == \"ROGUE\") then\n            if (specID == 259) then \n                local _, _, _, using_Blindside = GetTalentInfo (1, 3, 1)\n                if (using_Blindside) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.35\n                end\n            end\n        elseif (class == \"PALADIN\") then\n            if IsSpellKnown(24275) then\n                modTable.EXEenabled = true\n                modTable.EXEpercent = 0.2\n            end\n        elseif (class == \"MONK\") then\n            if IsSpellKnown(322109) then\n                modTable.EXEenabled = true\n                modTable.EXEpercent = 0.15\n            end\n        elseif (class == \"DEATHKNIGHT\") then\n            if (specID == 252) then \n                local _, _, _, using_Soulreaper = GetTalentInfo (4, 3, 1)\n                if (using_Soulreaper) then\n                    modTable.EXEenabled = true\n                    modTable.EXEpercent = 0.35\n                end\n            end\n        end\n    end\n    \n    EXECUTE = EXECUTE or CreateFrame(\"frame\", \"EXECUTE\", UIParent)\n    local EXECUTE = EXECUTE\n    EXECUTE:RegisterEvent(\"PLAYER_EQUIPMENT_CHANGED\")\n    EXECUTE:RegisterEvent(\"SOULBIND_ACTIVATED\")\n    EXECUTE:SetScript(\"OnEvent\", function(self, event, ...) \n            modTable.UpdateEXETalent()\n    end)\n    \nend",
						["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    do\n        function envTable.UnitInExecuteRange (unitFrame)\n            if (modTable.config.EXEhbcon) then\n                Plater.SetNameplateColor (unitFrame, modTable.config.EXEhbcolor)\n            end\n            \n            if (modTable.config.EXEbdron) then\n                Plater.SetBorderColor (unitFrame, modTable.config.EXEbdrcolor)\n            end\n        end\n        \n        function envTable.UnitInExecuteAlertRange (unitFrame, divisorPercent)\n            if (modTable.config.EXEhdoff) then\n                unitFrame.healthBar.healthCutOff:Hide() \n                unitFrame.healthBar.executeRange:Hide()\n            else\n                envTable.UpdateHealthDivisor (unitFrame, divisorPercent)\n            end\n        end\n        \n        function envTable.UpdateHealthDivisor (unitFrame, divisorPercent)\n            local healthBar = unitFrame.healthBar\n            \n            healthBar.healthCutOff:Show()\n            healthBar.healthCutOff:SetVertexColor (DetailsFramework:ParseColors (modTable.config.EXEhdcolor))\n            \n            healthBar.executeRange:Show()\n            healthBar.executeRange:SetVertexColor (DetailsFramework:ParseColors (modTable.config.EXEhicolor))\n            \n            if (modTable.EXEenabled) then\n                healthBar.healthCutOff:ClearAllPoints()\n                healthBar.executeRange:ClearAllPoints()\n                \n                healthBar.healthCutOff:SetSize (healthBar:GetHeight(), healthBar:GetHeight())\n                healthBar.healthCutOff:SetPoint (\"center\", healthBar, \"left\", healthBar:GetWidth() * divisorPercent, 0)\n                \n                healthBar.executeRange:SetTexCoord (0, modTable.EXEpercent, 0, 1)\n                healthBar.executeRange:SetHeight (healthBar:GetHeight())\n                if (divisorPercent == modTable.EXEpercent) then\n                    healthBar.executeRange:SetPoint (\"left\", healthBar, \"left\", 0, 0)\n                    healthBar.executeRange:SetPoint (\"right\", healthBar.healthCutOff, \"center\")\n                else\n                    healthBar.executeRange:SetPoint (\"right\", healthBar, \"right\", 0, 0)\n                    healthBar.executeRange:SetPoint (\"left\", healthBar.healthCutOff, \"center\")\n                end\n            end\n        end\n    end\nend",
