						["Nameplate Removed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.updateGlow(unitFrame, true)\nend\n\n\n",
						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.updateGlow(unitFrame)\nend\n\n\n",
						["Initialization"] = "function (modTable)\n    \n    local color = \"red\"\n    local useCustomExecutePercent = false\n    local customExecutePercent = 0.2\n    \n    function modTable.updateGlow(unitFrame, forceStop)\n        --unitFrame.ExecuteGlowModFrame = nil\n        if not unitFrame.ExecuteGlowModFrame then\n            --unitFrame.ExecuteGlowModFrame = Plater.CreateNameplateGlow (unitFrame.healthBar, color)\n            unitFrame.ExecuteGlowModFrame = Plater.CreateIconGlow (unitFrame.healthBar, color)\n        end\n        \n        if forceStop then\n            unitFrame.ExecuteGlowModFrame:Hide()\n            return\n        end\n        \n        local isExecute = false\n        if (useCustomExecutePercent) then\n            \n            --manual detection\n            local healthBar = unitFrame.healthBar\n            if (healthBar.CurrentHealth / healthBar.CurrentHealthMax <= customExecutePercent) then\n                isExecute = true                \n            end        \n            \n        else\n            \n            --auto detection\n            if (unitFrame.InExecuteRange) then\n                isExecute = true\n            end\n            \n        end\n        \n        if isExecute  then\n            if not unitFrame.ExecuteGlowModFrame:IsShown() then\n                unitFrame.ExecuteGlowModFrame:Show()\n            end\n        else\n            unitFrame.ExecuteGlowModFrame:Hide()\n        end\n    end\n    \nend\n\n\n",
